                                        {{ strlen(Auth::user()->username) > 6 ? substr(Auth::user()->username, 0, 6) : Auth::user()->username }}
                                    </span>
                                </div>
                                <div class="var-hover-overlay"></div>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    @else
        <div class="w-360 flex items-center justify-end">
